import React, {Component} from 'react';
import './App.css';
import 'h8k-components';
import ProductList from "./components/product-list";
import Cart from "./components/cart";
const title = "HackerShop";

class App extends Component {
    constructor() {
        super();

        const products = [...PRODUCTS].map((product, index) => {
            product.id = index ;
            product.image = `/images/items/${product.name.toLocaleLowerCase()}.png`;
            product.cartQuantity = 0;
            return product;
        });
        
        this.state = {
            cart: {
                items: [
                    {
                    name:"hani",
                    cartQuantity: 2
                },
            ]
            },
            products,
            test: "test"
        }
        
    }
    
    componentDidMount(){
        console.log(this.state.products[1]);
    }
    changeUnit = (unit) =>{
        this.setState({test: unit})
    }
    onAddQt = () => {
        this.setState(state => {
          const products = state.products.map(product => product.cartQuantity + 1);
    
        });
      };

    render() {
        return (
            <div>
                <h8k-navbar header={title}></h8k-navbar>
                <div className="layout-row shop-component">
                    <ProductList data={{products:this.state.products, changeUnit: this.changeUnit.bind(this), test: this.state.test}} />
                    <Cart cart={this.state.cart}/>
                </div>
            </div>
        );
    }
}

export const PRODUCTS = [
    {
        name: "Cap",
        price: 5
    },
    {
        name: "HandBag",
        price: 30
    },
    {
        name: "Shirt",
        price: 35
    },
    {
        name: "Shoe",
        price: 50
    },
    {
        name: "Pant",
        price: 35
    },
    {
        name: "Slipper",
        price: 25
    }
];
export default App;
